from django.conf.urls import url

from wagtail.users.views import users
from home import controller
from wagtail.users.views.users import delete

app_name = 'wagtailusers_users'
urlpatterns = [
    url(r'^$', users.index, name='index'),
    url(r'^add/$', controller.UserCreate.as_view(), name='add'),
    url(r'^(?P<pk>[-\w]+)/$', controller.UserUpdate.as_view(), name='edit'),
    url(r'^(?P<user_id>[-\w]+)/delete/$', delete, name='delete'),
]
